    async def _process_ai_request(self, message, system_prompt, processing_string_key):
        reply = await message.get_reply_message()
        if not reply:
            await utils.answer(message, self.strings["no_reply"])
            return

        user_text = ""
        if reply.text:
            user_text = reply.text
        elif reply.photo:
            user_text = "–≠—Ç–æ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ."
        elif reply.document and reply.document.mime_type.startswith("image/gif"):
            user_text = "–≠—Ç–æ GIF-–∞–Ω–∏–º–∞—Ü–∏—è."
        elif reply.video:
            user_text = "–≠—Ç–æ –≤–∏–¥–µ–æ."
        elif reply.sticker:
            user_text = "–≠—Ç–æ —Å—Ç–∏–∫–µ—Ä."
        elif reply.audio:
            user_text = "–≠—Ç–æ –∞—É–¥–∏–æ—Ñ–∞–π–ª."
        elif reply.voice:
            user_text = "–≠—Ç–æ –≥–æ–ª–æ—Å–æ–≤–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ."
        elif reply.document and reply.document.mime_type.startswith("video"):
            user_text = "–≠—Ç–æ –≤–∏–¥–µ–æ—Ñ–∞–π–ª."
        else:
            user_text = "–≠—Ç–æ —Ñ–∞–π–ª."


        if not user_text:
            await utils.answer(message, self.strings["no_text"])
            return

        await utils.answer(message, self.strings[processing_string_key])

        api_key = self.config["api_key"]
        if not api_key:
            await utils.answer(message, self.strings["no_api_key"])
            return

        headers = {
            "Authorization": f"Bearer {api_key}",
            "Content-Type": "application/json"
        }

        payload = {
            "model": self.model,
            "messages": [
                {
                    "role": "system",
                    "content": system_prompt
                },
                {
                    "role": "user",
                    "content": user_text
                }
            ],
            "temperature": self.config["temperature"],
            "top_p": self.config["top_p"],
            "max_tokens": self.config["max_tokens"],
            "presence_penalty": self.config["presence_penalty"],
            "frequency_penalty": self.config["frequency_penalty"],
        }

        try:
            async with aiohttp.ClientSession() as session:
                async with session.post(self.api_url, headers=headers, json=payload, timeout=120) as response:
                    if response.status == 200:
                        data = await response.json()
                        ai_response = data['choices'][0]['message']['content']
                        await utils.answer(message, ai_response)
                    else:
                        error_text = await response.text()
                        await utils.answer(message, self.strings["api_error"].format(f"–ö–æ–¥ –æ—à–∏–±–∫–∏: {response.status}\n{error_text}"))
        except Exception as e:
            await utils.answer(message, self.strings["api_error"].format(str(e)))


